rule.example:
public class Something {
  private static int FOO = 2; // Unused
  private int i = 5; // Unused
  private int j = 6;

  public int addOne() {
    return j++;
  }
}

public int doSomething() {
  int i = 5; // Unused
  int j = 6;
  j += 3;
  return j;
}

public class Something {
 private void foo() {} // unused
}

public class Foo {
 private void bar(String howdy) {
  // howdy is not used
 }
